import React, { useState } from "react";
import "./Header.scss";
import { useNavigate } from "react-router-dom";
import { Link, NavLink } from "react-router-dom";
import burger from "../../../../shared/assets/icons/burger-menu.png";
import { use } from "framer-motion";

const Header = () => {
  // states
  const navigate = useNavigate();
  const [open , setOpen] = useState(false)
  console.log(open);
  return (
    <div className="header">
      <div className="header_logo">
        <h3 className="logo_title">Logo</h3>
      </div>
      <div className="header_menu">
        <div class="box">
          <div id="hamburger" className={`${open ? "close" : "now-active"}`} onClick={() => setOpen(!open)}>
            <samp></samp>
            <samp></samp>
            <samp></samp>
          </div>
        </div>

        <ul  className={`${open ? "menu_list" : "now-active"}`}>
          <NavLink to="/" className="menu_item">
            Home
          </NavLink>
          <NavLink to="/skills/" className="menu_item">
            Experience
          </NavLink>
          <NavLink to="/project/" className="menu_item">
            Projects
          </NavLink>
        </ul>
      </div>
    </div>
  );
};

export default Header;
