import React, { useState } from "react";
import styles from "./Contact.module.scss";
import emailjs from "@emailjs/browser";
import { useRef } from "react";
import { toast } from "react-toastify";
import axios from "axios";

const Contact = () => {
  // states
  const form = useRef();
  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
  const [message, setMessage] = useState("");
  const [required, setRequired] = useState(false);

  // functions
  const sendEmail = async (e) => {
    const serviceId = "service_90xjcsj";
    const templateId = "template_ql99af8";
    const publicKey = "WOHXWWQ3wOpvF-0uy";
    e.preventDefault();

    const data = {
      service_id: serviceId,
      template_id: templateId,
      user_id: publicKey,
      template_params: {
        from_name: name,
        from_email: email,
        to_name: "Aiba",
        message: message,
      },
    };
    if (name === "" && email === "" && message === "") {
      setRequired(false);
    } else {
      try {
        const res = await axios.post(
          "https://api.emailjs.com/api/v1.0/email/send",
          data
        );
        toast.success("Message sent successfully");
        setEmail("");
        setMessage("");
        setName("");
      } catch (err) {
        console.log(err);
      }
    }
  };
  const setState = (value , setState) =>{
    setState(value);
  }

  return (
    <>
      <form ref={form} className={styles.contact} onSubmit={sendEmail}>
        <div>
          <h3 className={styles.contact_title}>Letâ€™s talk.</h3>
          <p className={styles.contact_text}>
            New projects, freelance inquiry or even a coffee.
          </p>
        </div>
        <div className={styles.contact_fields}>
          <div className={styles.field}>
            <label for="name" className={styles.field_title}>
              Name
            </label>
            <input
              style={{
                borderColor:
                  required && name === ""
                    ? "linear-gradient(270deg, #f06449, #ef3636)"
                    : "silver",
              }}
              className={styles.field_input}
              type="text"
            />
          </div>
          <div className={styles.field}>
            <label for="email" className={styles.field_title}>
              Email
            </label>
            <input className={styles.field_input} type="email" />
          </div>
          <div className={styles.field}>
            <label for="message" className={styles.field_title}>
              Message
            </label>
            <textarea
              className={styles.field_texterea}
              rows="5"
              cols="33"
            ></textarea>
          </div>
        </div>
        <input
          className={styles.contact_btn}
          type="submit"
          value="Send Message"
        />
      </form>
    </>
  );
};

export default Contact;
