import React from "react";
import styles from "./Contact.module.scss";
import emailjs from '@emailjs/browser';
import { useRef } from "react";

const Contact = () => {

  // states
  const form = useRef();

  // functions
  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm('service_90xjcsj', 'template_ql99af8', form.current, {
        publicKey: 'WOHXWWQ3wOpvF-0uy',
      })
      .then(
        () => {
          console.log('SUCCESS!');
        },
        (error) => {
          console.log('FAILED...', error.text);
        },
      );
  };
