import React from "react";
import styles from "./Contact.module.scss";
import emailjs from '@emailjs/browser';
import { useRef } from "react";
import { toast } from "react-toastify";


const Contact = () => {

  // states
  const form = useRef();

  // functions
  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm('service_90xjcsj', 'template_ql99af8', form.current, {
        publicKey: 'WOHXWWQ3wOpvF-0uy',
      })
      .then(
        () => {
          console.log('SUCCESS!');
        },
        (error) => {
          console.log('FAILED...', error.text);
        },
      );
  };
  return (
    <form ref={form} className={styles.contact} onSubmit={sendEmail}>
      <div>
        <h3 className={styles.contact_title}>Letâ€™s talk.</h3>
        <p className={styles.contact_text}>
          New projects, freelance inquiry or even a coffee.
        </p>
      </div>
      <div className={styles.contact_fields}>
        <div className={styles.field}>
          <label for="name" className={styles.field_title}>
            Name
          </label>
          <input className={styles.field_input} type="text" name="from_name" />
        </div>
        <div className={styles.field}>
          <label for="email" className={styles.field_title}>
            Email
          </label>
          <input className={styles.field_input} type="email" name="from_email" />
        </div>
        <div className={styles.field}>
          <label for="message" className={styles.field_title}>
            Message
          </label>
          <textarea className={styles.field_texterea} name="message" rows="5"cols="33"></textarea>
        </div>
      </div>
      <input className={styles.contact_btn} type="submit" value="Send Message"/>
    </form>
  );
};

export default Contact;
