import React, { useState } from "react";
import styles from "./Header.module.scss";
import enter_logo from "../../../../shared/assets/gif/enter";
import leave_logo from "../../../../shared/assets/gif/leave";

const Header = () => {
  // states
  const [isEntered, setIsEntered] = useState(false);
  const [isExited, setIsExited] = useState(false);

  // functions

  const handleEnter = () => {
    setIsEntered(true);
    setIsExited(false);
  };

  const handleExit = () => {
    setIsExited(true);
    setIsEntered(false);
  };
  return (
    <div className={styles.header}>
      <div className={styles.logo}>
        <video
          src={isEntered ? enter_logo : leave_logo}
          muted
          autoPlay
          loop
          onEnded={() => setIsExited(false)}
          onPlay={() => setIsEntered(true)}
          onPause={() => setIsEntered(false)}
          style={{ width: "100%", height: "100%" }}
        />
      </div>
      <div className={styles.social}></div>
    </div>
  );
};

export default Header;
